mapper.select:
select *
from (select row_number() over(order by bno desc) AS ronum, bno, title, content, writer,
             regDate, count, reco
      from board) as b
where ronum between #{rowStart} and #{rowEnd}
order by bno desc

mapper.update:
update board
set count = count + 1
where bno = #{bno}

update board
set reco = reco + 1
where bno = #{bno}

mapper.delete:
delete b, r
from board as b
inner join reply as r
on b.bno = r.bno
where b.bno= #{bno}
and r.bno = b.bno;

mapper.update:
update board
set title=#{title}, content=#{content}, writer=#{writer}
where bno = #{bno}

mapper.select:
select *
from (select row_number() over () AS ronum, rno, rcontent, rwriter, regDate,
             up, down, bno, mno
      from reply) as b
where ronum between #{rowStart} and #{rowEnd}
and bno = #{bno}
order by bno desc

mapper.update:
update reply
set up = up + 1
where bno = #{bno} and rno = #{rno}

update reply
set down = down + 1
where bno = #{bno} and rno = #{rno}

mapper.select:
select count(*)
from reply
where bno = #{bno}
